import React, { Component } from 'react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as modulosActions from '../../../../actions/modulosActions';

class CrearModulo extends Component {



    crear = () => {

        let data = {
            "id": this.props.lastId + 1,
            "texto": document.getElementById('texto_modulo').value
        }
        this.props.crearModulo(data, this.props.modulos);

    }

    componentDidMount() {


    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Crear módulo</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">

                                    <div className="input-field col s6">
                                        <input id="texto_modulo" type="text" className="validate" />
                                        <label htmlFor="texto_modulo">Título de la módulo</label>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/modulos'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/modulos'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.modulosReducers;
}
export default connect(mapToStateProps, modulosActions)(CrearModulo);
