import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');

export const traerRecursos = () => async (dispacth) => {
    dispacth({
        type: 'cargando'       
    })
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'recursos',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get(URL_DEFAULT + 'recursos');    
    
    dispacth({
        type: 'traer_recursos',
        payload: respuesta.data.recursos
    })
}

export const setFile = (file) => async (dispacth) => {
    console.log(file);
    dispacth({
        type: 'set_file',
        payload: file
    })
}

export const setType = (type) => async (dispacth) => {
    
    dispacth({
        type: 'set_type',
        payload: type
    })
}
export const crearRecurso = (recurso, recursos) => async (dispacth) => {
    const formData = new FormData();
    formData.append('file', recurso.file)    
    formData.append('name', recurso.name)
    formData.append("size", recurso.size)
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'recursos/createResource',        
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.post(URL_DEFAULT + 'recursos/createResource', formData);
    console.log(respuesta.data);
    recursos.push(respuesta.data.resource);
    dispacth({
        type: 'crear_recurso',
        payload: recursos
    })

    
}

export const editarRecurso = (data, recursos) => async (dispacth) => {
    const formData = new FormData();
    formData.append('id', data.id);
    formData.append('file', data.file);
    formData.append('name', data.name);
    formData.append("size", data.size);

    const instance = axios.create({
        baseURL: URL_DEFAULT + 'recursos/updateResource',        
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post(URL_DEFAULT + 'recursos/updateResource', formData);
    const respuesta = await instance.get(URL_DEFAULT + 'recursos');     

    dispacth({
        type: 'editar_recurso',
        payload: respuesta.data.recursos
    })

   
}

export const eliminarRecurso = (id) => async (dispacth) => {

    const instance = axios.create({
        baseURL: URL_DEFAULT + 'recursos/deleteResource',       
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post(URL_DEFAULT + 'recursos/deleteResource', { "id": id });
    
    const respuesta = await instance.get(URL_DEFAULT + 'recursos');  
    dispacth({
        type: 'eliminar_recurso',
        payload: respuesta.data.recursos
    })
    
}
