import React, { Component } from 'react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as recursosActions from '../../../../actions/recursosActions';


class CrearRecurso extends Component {



    crear = () => {


        let data = {
            "id": this.props.lastId + 1,
            "nombre": document.getElementById('nombre_recurso').value,
            "url": window.localStorage.getItem('imgData'),
            "tamano": window.localStorage.getItem('size') / 1000
        }
        this.props.crearRecurso(data, this.props.recursos);

    }

    handleChange = (e) => {
        var file = e.target.files[0];
        console.log(e.target.result);
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(e.target.files[0]);
        window.localStorage.setItem('imgData', output.src);
        window.localStorage.setItem('size', file.size);        
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="nombre_recurso" type="text" className="validate" />
                                            <label htmlFor="nombre_recurso">Nombre de la recurso</label>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="url_recurso" onChange={e => this.handleChange(e)} type="file" className="validate" />
                                        </div>
                                    </div>
                                </form>
                                <div class="card">
                                    <div class="card-image">
                                        <img id="output" className="col l6" />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/recursos'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/recursos'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.recursosReducers;
}
export default connect(mapToStateProps, recursosActions)(CrearRecurso);
