import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';

const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'tarjetas';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerTarjetasSM = (tarjetas) => (dispacth) => {       
    
    dispacth({
        type: 'traer_tarjetas_sm',
        payload: tarjetas
    })
}

export const crearTarjetaSM = (tarjeta, tarjetas) => async (dispacth) => {
    
   
    tarjetas.push(tarjeta);
    dispacth({
        type: 'crear_tarjeta_sm',
        payload: tarjetas
    })
}

export const editarTarjetaSM = (data, tarjetas) => async (dispacth) => {
    
    tarjetas[data.index].texto = data.texto;
    tarjetas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_tarjeta_sm',
        payload: tarjetas
    })
}

export const eliminarTarjetaSM = (index, tarjetas) => async (dispacth) => {
    
    tarjetas.splice(index, 1);
    dispacth({
        type: 'eliminar_tarjeta_sm',
        payload: tarjetas
    })
}
