export const traerPreguntas = (preguntas) => (dispacth) => {       
    
    dispacth({
        type: 'traer_preguntas',
        payload: preguntas
    })
}

export const crearPregunta = (pregunta, preguntas) => async (dispacth) => {
    
   
    preguntas.push(pregunta);
    dispacth({
        type: 'crear_pregunta',
        payload: preguntas
    })
}

export const editarPregunta = (data, preguntas) => async (dispacth) => {
    console.log(data.index);
    preguntas[data.index].texto = data.texto;
    preguntas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_pregunta',
        payload: preguntas
    })
}

export const eliminarPregunta = (index, preguntas) => async (dispacth) => {
    
    preguntas.splice(index, 1);
    dispacth({
        type: 'eliminar_pregunta',
        payload: preguntas.preguntas
    })
}
