import axios from 'axios';
const token = window.localStorage.getItem('token');

export const traerRecursos = (recursos) => (dispacth) => {

    dispacth({
        type: 'traer_recursos',
        payload: recursos
    })
}

export const setFile = (file) => async (dispacth) => {
    console.log(file);
    dispacth({
        type: 'set_file',
        payload: file
    })
}
export const crearRecurso = (recurso, recursos) => async (dispacth) => {
    const formData = new FormData();
    formData.append('file', file)
    //traer id de usuario desde el local storage
    //console.log(localStorage.getItem('id'))
    formData.append('id_usuario', localStorage.getItem('id'))
    formData.append("file_name", file.name)
    const instance = axios.create({
        baseURL: 'http://localhost:8000/users/createResource',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.post('http://localhost:8000/users/createResource', recurso);
    console.log(respuesta.data);
    /*usuarios.push(respuesta.data.user);
    dispacth({
        type: 'crear_usuario',
        payload: usuarios
    })*/

    recursos.push(recurso);
    dispacth({
        type: 'crear_recurso',
        payload: recursos
    })
}

export const editarRecurso = (data, recursos) => async (dispacth) => {

    recursos[data.index].nombre = data.nombre;
    recursos[data.index].id = data.id;
    recursos[data.index].url = data.url;
    dispacth({
        type: 'editar_recurso',
        payload: recursos
    })
}

export const eliminarRecurso = (index, recursos) => async (dispacth) => {

    recursos.splice(index, 1);
    dispacth({
        type: 'eliminar_recurso',
        payload: recursos
    })
}
