import React, { Component } from 'react';
import './Contenido.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as usuariosActions from '../../../../actions/usuariosActions';
import Spinner from '../../general/Spinner';

class Contenido extends Component {

    componentDidMount() {
        this.props.traerTodos();

    }

    showTable = () => {
        if (this.props.cargando) {
            return <Spinner />
        }
        return (
            <div>
                <div className="a-right">
                    <Link to='/crear' id="crear_user" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>

                            <th scope="col">Nombres</th>

                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>

        )
    }
    ponerFilas = () => {
        return (
            <tr key={1}>

                <td>
                    {this.props.dataUser.person.name}
                </td>
                
            </tr>
        )
    }

    render() {

        return (

            <div id="contenido" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Datos personales</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.usuariosReducers;
}
export default connect(mapToStateProps, usuariosActions)(Contenido);
