import React, { Component } from 'react';
import './IndexPreguntasT.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasTriviaActions from '../../../../actions/preguntasTriviaActions';
import Spinner from '../../general/Spinner';

class IndexPreguntasT extends Component {

    getPregunta(preguntasT) {
        this.setState({
            preguntasT: preguntasT
        })
    }
    componentDidMount() {
        this.props.traerPreguntasT(this.props.preguntasT);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearPreguntaT' id="crear_preguntaT" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>                            
                            <th scope="col">Texto de la pregunta</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }
    ponerFilas = () => this.props.preguntasT.map((pregunta, index) => (
        <tr key={pregunta.id}>

            <th scope="row">
                {index + 1}
            </th>            
            <td>
                {pregunta.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarPreguntaT",
                    index: index,
                    id: pregunta.id,
                    texto: pregunta.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-preguntasT" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de preguntas</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasTReducers;
}
export default connect(mapToStateProps, preguntasTriviaActions)(IndexPreguntasT);
