import React, { Component } from 'react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as subtemasActions from '../../../../actions/subtemasActions';

class CrearSubtema extends Component {



    crear = () => {

        let data = {
            "id": this.props.lastId + 1,
            "texto": document.getElementById('texto_subtema').value,
            "modulo_id": document.getElementById('modulos').value
        }
        this.props.crearSubtema(data, this.props.subtemas);

    }

    showModulos = () => this.props.modulos.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))

    componentDidMount() {


    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Crear subtema</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s12">
                                            <p>Título del subtema:</p>
                                            <input id="texto_subtema" type="text" className="validate" />
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="input-field col s12">
                                            <p>¿A qué temática/módulo pertenece?</p>
                                            <select id="modulos" className="browser-default">
                                                {this.showModulos()}
                                            </select>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/subtemas'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/subtemas'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.subtemasReducers;
}
export default connect(mapToStateProps, subtemasActions)(CrearSubtema);
