import React, { Component } from 'react';
import './IndexTarjetas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as tarjetasActions from '../../../../actions/tarjetasActions';
import Spinner from '../../general/Spinner';

class IndexTarjetas extends Component {


    componentDidMount() {
        this.props.traerTarjetas(this.props.tarjetas);
        this.props.traerSubtemas();
    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    getNombreSubtema = (id) => {
        let nombre = '';
        this.props.subtemas.forEach(element => {
            if (element.id == id) {
                nombre = element.texto
            }
        });
        return nombre;
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearTarjeta' id="crear_tarjeta" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Prioridad/Orden</th>
                            <th scope="col">Texto del tarjeta</th>
                            <th scope="col">Título del tarjeta</th>
                            <th scope="col">Imagen</th>
                            <th scope="col">Subtema</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }
    ponerFilas = () => this.props.tarjetas.map((tarjeta, index) => (
        <tr key={tarjeta.id}>

            <th scope="row">
                {index + 1}
            </th>
            <td>
                {tarjeta.prioridad}
            </td>
            <td dangerouslySetInnerHTML={{ __html: tarjeta.texto }} />
            <td dangerouslySetInnerHTML={{ __html: tarjeta.titulo }} />
            <td>
                <img id="tableImg" src={tarjeta.path_imagen} alt="imagen"></img>
            </td>
            <td>
                {this.getNombreSubtema(tarjeta.subtema_id)}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarTarjeta",
                    index: index,
                    id: tarjeta.id,
                    texto: tarjeta.texto,
                    titulo: tarjeta.titulo,
                    path_imagen: tarjeta.path_imagen,
                    subtema_id: tarjeta.subtema_id,
                    prioridad: tarjeta.prioridad,
                    nroTarjetas: this.props.nroTarjetas
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-tarjetas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado tarjetas de conocimiento</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.tarjetasReducers;
}
export default connect(mapToStateProps, tarjetasActions)(IndexTarjetas);
