import axios from 'axios';
const token = window.localStorage.getItem('token');
const url_local = 'http://localhost:8000/api/';
const url_remote = 'https://www.test.pressstart.co/dev/whitelabel/api/';
const url_default = url_local;
export const traerRecursos = () => async (dispacth) => {

    const instance = axios.create({
        baseURL: url_default + 'resources',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.get(url_default + 'resources');    
    
    dispacth({
        type: 'traer_recursos',
        payload: respuesta.data.recursos
    })
    /*dispacth({
        type: 'traer_recursos',
        payload: recursos
    })*/
}

export const setFile = (file) => async (dispacth) => {
    console.log(file);
    dispacth({
        type: 'set_file',
        payload: file
    })
}

export const setType = (type) => async (dispacth) => {
    
    dispacth({
        type: 'set_type',
        payload: type
    })
}
export const crearRecurso = (recurso, recursos) => async (dispacth) => {
    const formData = new FormData();
    formData.append('file', recurso.file)    
    formData.append('name', recurso.name)
    formData.append("size", recurso.size)
    const instance = axios.create({
        baseURL: url_default + 'resoyrces/createResource',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.post(url_default + 'resources/createResource', formData);
    console.log(respuesta.data);
    recursos.push(respuesta.data.resource);
    dispacth({
        type: 'crear_recurso',
        payload: recursos
    })

    
}

export const editarRecurso = (data, recursos) => async (dispacth) => {
    const formData = new FormData();
    formData.append('id', data.id);
    formData.append('file', data.file);
    formData.append('name', data.name);
    formData.append("size", data.size);

    const instance = axios.create({
        baseURL: url_default + 'resources/updateResource',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post(url_default + 'resources/updateResource', formData);
    const respuesta = await instance.get(url_default + 'resources');     

    dispacth({
        type: 'editar_recurso',
        payload: respuesta.data.recursos
    })

   
}

export const eliminarRecurso = (id) => async (dispacth) => {

    const instance = axios.create({
        baseURL: url_default + 'resources/deleteResource',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post(url_default + 'resources/deleteResource', { "id": id });
    
    const respuesta = await instance.get(url_default + 'resources');  
    dispacth({
        type: 'eliminar_recurso',
        payload: respuesta.data.recursos
    })
    
}
