import axios from 'axios';

const token = window.localStorage.getItem('token');

export const traerTodos = () => async (dispacth) => {   
    const instance = axios.create({
        baseURL: 'http://localhost:8000/api/users',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.get('http://localhost:8000/api/users');    
    
    dispacth({
        type: 'traer_usuarios',
        payload: respuesta.data.users
    })
}

export const crearUsuario = (data, usuarios) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: 'http://localhost:8000/create',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.post('http://localhost:8000/create', data);   
    usuarios.push(respuesta.data.user);
    dispacth({
        type: 'crear_usuario',
        payload: usuarios
    })
}

export const editarUsuario = (data) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: 'http://localhost:8000/update',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post('http://localhost:8000/update', data);
    const respuesta = await instance.get('http://localhost:8000/api/users');     

    dispacth({
        type: 'editar_usuario',
        payload: respuesta.data.users
    })
}

export const eliminarUsuario = (id) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: 'http://localhost:8000/delete',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post('http://localhost:8000/delete', { "id": id });
    
    const respuesta = await instance.get('http://localhost:8000/api/users');  
    dispacth({
        type: 'eliminar_usuario',
        payload: respuesta.data.users
    })
}
