import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';

class CrearPregunta extends Component {


    constructor(props) {
        super(props);

        this.state = {
            data: '<p>Escriba el <em>texto</em> de la <strong>pregunta</strong></p>',
            positions: [],
            positionsLastSelect: [],
            scores: [],
            scoresLastSelect: [],

        };

        this.handleChange = this.handleChange.bind(this);
        this.onEditorChange = this.onEditorChange.bind(this);
    }

    onEditorChange(evt) {
        this.setState({
            data: evt.editor.getData()
        });
    }

    handleChange(changeEvent) {
        this.setState({
            data: changeEvent.target.value
        });
    }

    crear = () => {
        //let idPregunta = this.props.preguntas; 

        let data = {
            "texto": this.state.data,
            "modulo_id": document.getElementById('modulos').value,
            "enunciado": document.getElementById('enunciado').value,
            "respuestas": [
                {
                    'texto': document.getElementById('texto_respuesta_1').value,
                    "posicion": document.getElementById('pos_resp_1').value,
                    "puntaje": document.getElementById('score_resp_1').value
                },
                {
                    'texto': document.getElementById('texto_respuesta_2').value,
                    "posicion": document.getElementById('pos_resp_2').value,
                    "puntaje": document.getElementById('score_resp_2').value
                },
                {
                    'texto': document.getElementById('texto_respuesta_3').value,
                    "posicion": document.getElementById('pos_resp_3').value,
                    "puntaje": document.getElementById('score_resp_3').value
                }
            ]
        }
        console.log(data);
        this.props.crearPregunta(data, this.props.preguntas);

    }

    positionChange = (e, positions) => {
        let postionsEnables = positions;
        let pos = postionsEnables.indexOf(e.target.value);
        postionsEnables.splice(pos, 1);
        if (e.target.id == 'pos_resp_1') {
            this.setState({
                positions: postionsEnables
            });
        } else {
            this.setState({
                positionsLastSelect: postionsEnables
            });
        }

    }

    scoreChange = (e, scores) => {
        let scoresEnables = scores;
        console.log(e.target.value);
        let pos = scoresEnables.indexOf(e.target.value);
        scoresEnables.splice(pos, 1);
        if (e.target.id == 'score_resp_1') {
            this.setState({
                scores: scoresEnables
            });
        } else {
            this.setState({
                scoresLastSelect: scoresEnables
            });
        }

    }

    showOptions = (options) => (
        options.map((op, index) => (
            <option key={index} value={op}>{op}</option>
        ))
    )

    showModulos = () => this.props.modulos.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))

    showScores = (e, item) => {

        console.log(e.target.value);
    }

    render() {

        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Crear pregunta</h5></div>
                        <div className="card-content">
                            <span className="card-title"><h4>Pregunta</h4></span>
                            <div className="row">
                                <form className="col s12">
                                    <div class="row">
                                        <div class="input-field col s12">
                                            <label for="textarea1">Enunciado</label>
                                            <textarea id="enunciado" class="materialize-textarea"></textarea>
                                        </div>
                                    </div>
                                    <CKEditor
                                        data={this.state.data}
                                        onChange={this.onEditorChange} />
                                </form>
                            </div>
                            <div className="row">
                                <div className="input-field col s6">
                                    <p>Seleccione el m贸dulo al que pertenece esta pregunta:</p>
                                    <select id="modulos" className="browser-default">
                                        {this.showModulos()}
                                    </select>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="card-panel">
                        <div className="card-content">
                            <span className="card-title"><h4>Respuestas</h4></span>
                            <div className="row">
                                <form className="col s12">
                                    <div className="input-field col s1">
                                        <select id="pos_resp_1" className="browser-default" onChange={(e) => { this.positionChange(e, ['a', 'b', 'c']) }}>
                                            <option value="">Posici贸n</option>
                                            <option value="a">a</option>
                                            <option value="b">b</option>
                                            <option value="c">c</option>
                                        </select>
                                    </div>
                                    <div className="input-field col s10">
                                        <input id="texto_respuesta_1" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                    <div className="input-field col s1">
                                        <select id="score_resp_1" className="browser-default" onChange={(e) => { this.scoreChange(e, ['0', '1', '2']) }}>
                                            <option value="" >Puntaje</option>
                                            <option value="0">0</option>
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                        </select>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="input-field col s1">
                                        <select id='pos_resp_2' className="browser-default" onChange={(e) => { this.positionChange(e, this.state.positions.slice()) }}>
                                            <option value="default">Posici贸n</option>
                                            {this.showOptions(this.state.positions)}
                                        </select>
                                    </div>
                                    <div className="input-field col s10">
                                        <input id="texto_respuesta_2" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                    <div className="input-field col s1">
                                        <select id="score_resp_2" className="browser-default" onChange={(e) => { this.scoreChange(e, this.state.scores.slice()) }}>
                                            <option value="">Puntaje</option>
                                            {this.showOptions(this.state.scores)}
                                        </select>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="input-field col s1">
                                        <select id='pos_resp_3' className="browser-default">
                                            <option value="">Posici贸n</option>
                                            {this.showOptions(this.state.positionsLastSelect)}
                                        </select>
                                    </div>
                                    <div className="input-field col s10">
                                        <input id="texto_respuesta_3" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                    <div className="input-field col s1">
                                        <select id="score_resp_3" className="browser-default">
                                            <option value="default">Puntaje</option>
                                            {this.showOptions(this.state.scoresLastSelect)}
                                        </select>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntas'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/preguntas'>Cancelar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(CrearPregunta);
