import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';

class CrearPregunta extends Component {


    constructor( props ) {
        super( props );

        this.state = {
            data: '<p>React is really <em>nice</em>!</p>'
        };

        this.handleChange = this.handleChange.bind( this );
        this.onEditorChange = this.onEditorChange.bind( this );
    }

    onEditorChange( evt ) {
        this.setState( {
            data: evt.editor.getData()
        } );
        console.log(this.state.data);
    }

    handleChange( changeEvent ) {
        this.setState( {
            data: changeEvent.target.value
        } );
    }

    crear = () => {
        //let idPregunta = this.props.preguntas; 
        console.log(this.props.preguntas);
        
        /*let data = {
            "id": this.props.lastId + 1,
            "texto": document.getElementById('texto_pregunta').value
        }
        this.props.crearPregunta(data, this.props.preguntas);*/

    }

    componentDidMount() {


    }

    /*ponerGeneros = () => this.state.generos.map((genero, index) => (
        <option value={genero.id_genero}>
            {genero.nombre_genero}
        </option>
    ));*/

    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                <label>Texto de la pregunta</label>
                                <CKEditor id="texto_pregunta"
                                            data={this.state.data}
                                            onChange={this.onEditorChange} />
                                              
                                    {/*<label>Texto de la pregunta</label>
                                    <div className="input-field col s12">
                                        <input id="texto_pregunta" type="text" className="validate" />

                                                                            
                                    </div>*/}
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntas'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(CrearPregunta);
