import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';

class EditarPregunta extends Component {

    constructor(props) {
        super(props);
        this.state = {
            texto: this.props.location.texto,
            enunciado: this.props.location.enunciado,
            positions: ['a', 'b', 'c'],
            positionsLastSelect: ['a', 'b', 'c'],
            scores: ['0', '1', '2'],
            scoresLastSelect: ['0', '1', '2']
        };

        this.handleChange = this.handleChange.bind(this);
        this.onEditorTextoChange = this.onEditorTextoChange.bind(this);
    }
    componentDidMount() {
        this.props.traerRespById(this.props.location.id);
    }
    componentWillMount() {
        this.props.clearAllValues();
    }
    onEditorTextoChange(evt) {
        this.setState({
            texto: evt.editor.getData()
        });
    }

    handleChange(changeEvent) {
        this.setState({
            data: changeEvent.target.value
        });
    }

    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": this.state.texto,
            "modulo_id": document.getElementById('modulos').value,
            "enunciado": document.getElementById('enunciado').value,
            "respuestas": [
                {
                    "id": document.getElementById('id_resp_1').value,
                    "texto": document.getElementById('texto_respuesta_1').value,
                    "posicion": document.getElementById('pos_resp_1').value,
                    "puntaje": document.getElementById('score_resp_1').value
                },
                {
                    "id": document.getElementById('id_resp_2').value,
                    "texto": document.getElementById('texto_respuesta_2').value,
                    "posicion": document.getElementById('pos_resp_2').value,
                    "puntaje": document.getElementById('score_resp_2').value
                },
                {
                    "id": document.getElementById('id_resp_3').value,
                    "texto": document.getElementById('texto_respuesta_3').value,
                    "posicion": document.getElementById('pos_resp_3').value,
                    "puntaje": document.getElementById('score_resp_3').value
                }
            ]
        }
        this.props.editarPregunta(data);
    }

    positionChange = (e, positions) => {
        let postionsEnables = positions;
        let pos = postionsEnables.indexOf(e.target.value);
        postionsEnables.splice(pos, 1);
        if (e.target.id == 'pos_resp_1') {
            this.setState({
                positions: postionsEnables
            });
        } else {
            this.setState({
                positionsLastSelect: postionsEnables
            });
        }
        this.props.clearPositionsDefaults();
    }

    scoreChange = (e, scores) => {
        let scoresEnables = scores;
        let pos = scoresEnables.indexOf(e.target.value);
        scoresEnables.splice(pos, 1);
        if (e.target.id == 'score_resp_1') {
            this.setState({
                scores: scoresEnables
            });
        } else {
            this.setState({
                scoresLastSelect: scoresEnables
            });
        }
        this.props.clearScoresDefaults();
    }

    showOptions = (options) => (
        options.map((op, index) => (
            <option key={index} value={op}>{op}</option>
        ))
    )

    delete = () => {
        this.props.eliminarPregunta(this.props.location.id);
    }

    showModulos = () => this.props.modulos.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))

    textChange = (e) => {

    }

    render() {

        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Editar pregunta</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div class="row">
                                        {/*<div class="input-field col s12">
                                            <p>Enunciado</p>
                                            <textarea id="enunciado" class="materialize-textarea" defaultValue={this.props.location.enunciado}></textarea>
        </div>*/}
                                         <label>Enunciado</label>
                                        <CKEditor
                                            data={this.state.enunciado}
                                            onChange={this.onEditorEnunciadoChange} />
                                    </div>
                                    <div className="row">
                                        <label>Texto de la pregunta</label>
                                        <CKEditor
                                            data={this.state.texto}
                                            onChange={this.onEditorTextoChange} />
                                    </div>
                                </form>
                            </div>
                            <div className="row">
                                <div className="input-field col s6">
                                    <p>Módulo :</p>
                                    <select id="modulos" className="browser-default" defaultValue={this.props.location.modulo}>
                                        {this.showModulos()}
                                    </select>
                                </div>
                            </div>
                        </div>
                        <div className="card-panel">
                            <div className="card-content">
                                <span className="card-title"><h4>Respuestas</h4></span>
                                <div className="row">
                                    <form className="col s12">
                                        <input id="id_resp_1" type="hidden" value={this.props.ids[0]}></input>
                                        <div className="input-field col s1">
                                            <select id="pos_resp_1" className="browser-default" value={this.props.positionsDefaults[0]} onChange={(e) => { this.positionChange(e, ['a', 'b', 'c']) }}>
                                                <option value="">Posición</option>
                                                {this.showOptions(['a', 'b', 'c'])}
                                            </select>
                                        </div>
                                        <div className="input-field col s10">
                                            <input id="texto_respuesta_1" type="text" className="validate" defaultValue={this.props.textsDefaults[0]} onChange={this.textChange} />
                                        </div>
                                        <div className="input-field col s1">
                                            <select id="score_resp_1" className="browser-default" value={this.props.scoresDefaults[0]} onChange={(e) => { this.scoreChange(e, ['0', '1', '2']) }}>
                                                <option value="" >Puntaje</option>
                                                {this.showOptions(['0', '1', '2'])}
                                            </select>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <div className="card-content">
                                <div className="row">
                                    <form className="col s12">
                                        <input id="id_resp_2" type="hidden" value={this.props.ids[1]}></input>
                                        <div className="input-field col s1">
                                            <select id='pos_resp_2' className="browser-default" value={this.props.positionsDefaults[1]} onChange={(e) => { this.positionChange(e, this.state.positions.slice()) }}>
                                                <option value="default">Posición</option>
                                                {this.showOptions(this.state.positions)}
                                            </select>
                                        </div>
                                        <div className="input-field col s10">
                                            <input id="texto_respuesta_2" type="text" className="validate" defaultValue={this.props.textsDefaults[1]} onChange={this.textChange} />
                                        </div>
                                        <div className="input-field col s1">
                                            <select id="score_resp_2" className="browser-default" value={this.props.scoresDefaults[1]} onChange={(e) => { this.scoreChange(e, this.state.scores.slice()) }}>
                                                <option value="">Puntaje</option>
                                                {this.showOptions(this.state.scores)}
                                            </select>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <div className="card-content">
                                <div className="row">
                                    <form className="col s12">
                                        <input id="id_resp_3" type="hidden" value={this.props.ids[2]}></input>
                                        <div className="input-field col s1">
                                            <select id='pos_resp_3' className="browser-default" value={this.props.positionsDefaults[2]}>
