import React, { Component } from 'react';
import './IndexRespuestasT.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as respuestasTriviaActions from '../../../../actions/respuestasTriviaActions';
import Spinner from '../../general/Spinner';

class IndexRespuestasT extends Component {


    componentDidMount() {
        this.props.traerRespuestasT(this.props.respuestasT);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearRespuestaT' id="crear_respuestaT" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>                            
                            <th scope="col">Texto de la respuesta</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }
    ponerFilas = () => this.props.respuestasT.map((respuesta, index) => (
        <tr key={respuesta.id}>

            <th scope="row">
                {index + 1}
            </th>            
            <td>
                {respuesta.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarRespuestaT",
                    index: index,
                    id: respuesta.id,
                    texto: respuesta.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-respuestasT" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de respuestas</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.respuestasTReducers;
}
export default connect(mapToStateProps, respuestasTriviaActions)(IndexRespuestasT);
