import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as recursosActions from '../../../../actions/recursosActions';

class EditarRecurso extends Component {

    
    editar = () => {

        let data = {
            "index": this.props.location.index,
            "id": this.props.location.id,
            "nombre": document.getElementById('nombre_recurso').value,
            "url": document.getElementById('url_recurso').value
        }

        this.props.editarRecurso(data, this.props.recursos);
    }

    delete() {
       
            this.props.eliminarRecurso(this.props.location.index, this.props.recursos);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="nombre_recurso" type="text" className="validate" placeholder={this.props.location.nombre} defaultValue={this.props.location.nombre} />                                            
                                        </div>                                        
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="url_recurso" type="text" className="validate" placeholder={this.props.location.url} defaultValue={this.props.location.url} />                                            
                                        </div>                                        
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="url_recurso" onChange={e => this.handleChange(e)} type="file" className="validate" />
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/recursos'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/recursos'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/recursos' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                        <img id="output" src={this.props.location.url}/>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.recursosReducers;
}
export default connect(mapToStateProps, recursosActions)(EditarRecurso);
