import React, { Component } from 'react';
import './IndexRespuestas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as respuestasActions from '../../../../actions/respuestasActions';
import Spinner from '../../general/Spinner';

class IndexRespuestas extends Component {


    componentDidMount() {
        this.props.traerRespuestas(this.props.respuestas);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearRespuesta' id="crear_respuesta" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>                            
                            <th scope="col">Texto de la respuesta</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }
    ponerFilas = () => this.props.respuestas.map((respuesta, index) => (
        <tr key={respuesta.id}>

            <th scope="row">
                {index + 1}
            </th>           
            <td>
                {respuesta.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarRespuesta",
                    index: index,
                    id: respuesta.id,
                    texto: respuesta.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-respuestas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de respuestas</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.respuestasReducers;
}
export default connect(mapToStateProps, respuestasActions)(IndexRespuestas);
