import React, { Component } from 'react';
import './Crear.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { throwStatement } from '@babel/types';

const token = window.localStorage.getItem('token');

class CrearPregunta extends Component {

    constructor(props) {
        super(props);
        this.state = {
            generos: []
        }
    }

    crear = () => {

        let data = {
            "nombres": document.getElementById('nombres').value,
            "apellidos": document.getElementById('apellidos').value,
            "usuario": document.getElementById('usuario').value,
            "genero": document.getElementById('genero').value,
            "nombre_negocio": document.getElementById('nombre_negocio').value,
            "tipo_negocio": document.getElementById('tipo_negocio').value,
            "ciudad": document.getElementById('ciudad').value,
            "localidad": document.getElementById('localidad').value
        }
        const instance = axios.create({
            baseURL: 'http://localhost:8000/api/create',
            timeout: 1000,
            headers: { 'Authorization': 'Bearer ' + token }
        });

        instance.post('http://localhost:8000/api/create', data)
            .then(res => {
                console.log(res.data);
            });

    }

    componentDidMount() {


    }

    /*ponerGeneros = () => this.state.generos.map((genero, index) => (
        <option value={genero.id_genero}>
            {genero.nombre_genero}
        </option>
    ));*/

    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">

                                    <div className="input-field col s6">
                                        <input id="texto_pregunta" type="text" className="validate" />
                                        <label htmlFor="texto_pregunta">Texto de la pregunta</label>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default CrearPregunta;
