import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'answersTrivia';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});
export const traerRespuestasT = () => async (dispacth) => {       
    dispacth({
        type: 'cargando'        
    })
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_respuestasT',
        payload: respuesta.data.respuestas
    })
}

export const crearRespuestaT = (respuesta, respuestas) => async (dispacth) => {
    
   
    const r = await instance.post(url, respuesta);      
    respuestas.push(r.data.respuesta);
    dispacth({
        type: 'crear_respuestaT',
        payload: respuestas
    })
}

export const editarRespuestaT = (data) => async (dispacth) => {
    
    
    await instance.put(url, data);
    const respuesta = await instance.get();   
    
    dispacth({
        type: 'editar_respuestaT',
        payload: respuesta.data.respuestas
    })
}

export const eliminarRespuestaT = (id) => async (dispacth) => {
    
   
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get();  
    
    dispacth({
        type: 'eliminar_respuestaT',
        payload: respuesta.data.respuestas
    })
}
