const INITIAL_STATE = {
    preguntas: [],
    respuestas: [],
    cargando: false
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'traer_preguntas':
            return {
                ...state,
                preguntas: action.payload,
                cargando: false
            }
        case 'crear_pregunta':
            return {
                ...state,
                preguntas: action.payload
            }
        case 'editar_pregunta':
            return {
                ...state,
                preguntas: action.payload
            }
        case 'eliminar_pregunta':
            return {
                ...state,
                preguntas: action.payload
            }
        case 'cargando':
            return {
                ...state,
                cargando: true
            }
        case 'traer_respuestas':
            return {
                ...state,
                cargando: true
            }
        default:
            return state;
    }
}
