const INITIAL_STATE = {
    modulos: [
        {
            'id': 1,
            'texto': 'texto modulo 1'
        },
        {
            'id': 2,
            'texto': 'texto modulo 2'
        },
        {
            'id': 3,
            'texto': 'texto modulo 3'
        },
        {
            'id': 4,
            'texto': 'texto modulo 4'
        },
    ],
    lastId: 4
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'traer_modulos':
            return { ...state, modulos: action.payload }
        case 'crear_modulo':
            return {
                ...state,
                modulos: action.payload
            }
        case 'editar_modulo':
            return {
                ...state,
                modulos: action.payload
            }
        case 'eliminar_modulo':
            return {
                ...state,
                modulos: action.payload
            }
        default:
            return state;
    }
}
