import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import './CrearT.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasTriviaActions from '../../../../actions/preguntasTriviaActions';

class CrearPreguntaT extends Component {

    constructor(props) {
        super(props);

        this.state = {
            texto: '<p>Escriba el <em>texto</em> de la <strong>pregunta</strong></p>',

        };

        this.onEditorTextoChange = this.onEditorTextoChange.bind(this);

    }

    onEditorTextoChange(evt) {
        this.setState({
            texto: evt.editor.getData()
        });
    }

    crear = () => {

        let data = {
            "texto": this.state.texto,
            "feedback_verdadero": document.getElementById('feedbck_positivo').value,
            "feedback_negativo": document.getElementById('feedbck_negativo').value,
            "respuestas": [
                {
                    'texto': document.getElementById('resp1').value                    
                },
                {
                    'texto': document.getElementById('resp2').value
                }
            ],
            "opcion_correcta": document.getElementById('opc_correcta').value,
            "subtema_id": document.getElementById('subtemas').value
        }
        this.props.crearPreguntaT(data);

    }

    componentDidMount() {


    }

    showSubtemas = () => this.props.subtemas.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))

    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Crear pregunta</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="row">
                                        <p>Texto de la pregunta</p>
                                        <CKEditor
                                            data={this.state.texto}
                                            onChange={this.onEditorTextoChange} />
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntasT'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/preguntasT'>Cancelar</Link>
                        </div>
                    </div>
                    <div className="card-panel">
                        <div className="card-title"><h5>Feedbacks</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <div className="input-field col s12">
                                    <p>Feedback positivo:</p>
                                    <input id="feedbck_positivo" type="text" className="validate" />
                                </div>
                            </div>
                            <div className="row">
                                <div className="input-field col s12">
                                    <p>Feedback negativo:</p>
                                    <input id="feedbck_negativo" type="text" className="validate" />
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="card-panel">
                        <div className="card-title"><h5>Respuestas</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <div className="input-field col s12">
                                    <p>Respuesta 1:</p>
                                    <input id="resp1" type="text" className="validate" defaultValue="Verdadero" />
                                </div>
                            </div>
                            <div className="row">
                                <div className="input-field col s12">
                                    <p>Respuesta 2:</p>
                                    <input id="resp2" type="text" className="validate" defaultValue="Falso" />
                                </div>
                            </div>
                            <div className="row">
                                <div className="input-field col s6">
                                    <p>Opción correcta:</p>
                                    <select id="opc_correcta" className="browser-default">
                                        <option value="1">1</option>
                                        <option value="2">2</option>
                                    </select>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="card-panel">
                        <div className="card-title"><h5>subtema asociado</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <div className="input-field col s6">
                                    <p>¿A qué subtema pertenece?</p>
                                    <select id="subtemas" className="browser-default">
                                        {this.showSubtemas()}
                                    </select>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasTReducers;
}
export default connect(mapToStateProps, preguntasTriviaActions)(CrearPreguntaT);
