import React, { Component } from 'react';
import './IndexTarjetas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as tarjetasActions from '../../../../actions/tarjetasActions';

class IndexTarjetas extends Component {

    
    componentDidMount() {
        this.props.traerTarjetas(this.props.tarjetas);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    ponerFilas = () => this.props.tarjetas.map((tarjeta, index) => (
        <tr key={tarjeta.id}>

            <th scope="row">
                {index + 1}
            </th>
            <td>
                {tarjeta.id}
            </td>
            <td>
                {tarjeta.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarTarjeta",
                    index: index,
                    id: tarjeta.id,
                    texto: tarjeta.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-tarjetas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="a-right">
                                <Link to='/crearTarjeta' id="crear_tarjeta" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                            </div>
                            <table id="table-users">
                                <thead>
                                    <tr>
                                        <th scope="col">#</th>
                                        <th scope="col">Id</th>
                                        <th scope="col">Texto del tarjeta</th>
                                        <th scope="col">Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.ponerFilas()}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.tarjetasReducers;
}
export default connect(mapToStateProps, tarjetasActions)(IndexTarjetas);
