export const traerRecursos = (recursos) => (dispacth) => {       
    
    dispacth({
        type: 'traer_recursos',
        payload: recursos
    })
}

export const crearRecurso = (recurso, recursos) => async (dispacth) => {
    
   
    recursos.push(recurso);
    dispacth({
        type: 'crear_recurso',
        payload: recursos
    })
}

export const editarRecurso = (data, recursos) => async (dispacth) => {
    
    recursos[data.index].texto = data.texto;
    recursos[data.index].id = data.id;
    
    dispacth({
        type: 'editar_recurso',
        payload: recursos
    })
}

export const eliminarRecurso = (index, recursos) => async (dispacth) => {
    
    recursos.splice(index, 1);
    dispacth({
        type: 'eliminar_recurso',
        payload: recursos
    })
}
