import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';

const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'subtemas';
const urlModulos = URL_DEFAULT + 'modulos';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerSubtemas = () => async (dispacth) => {       
    dispacth({
        type: 'cargando'       
    })
    const respuesta = await instance.get();  
    
    dispacth({
        type: 'traer_subtemas',
        payload: respuesta.data.subtemas
    })
}

export const traerModulos = () => async (dispacth) => {       
    const instance = axios.create({
        baseURL: urlModulos,
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_modulos',
        payload: respuesta.data.modulos
    })
}

export const crearSubtema = (subtema, subtemas) => async (dispacth) => {
        
    const r = await instance.post(url, subtema);      
    subtemas.push(r.data.subtema);    
    dispacth({
        type: 'crear_subtema',
        payload: subtemas
    })
}

export const editarSubtema = (data) => async (dispacth) => {
      
    await instance.put(url, data);
    const respuesta = await instance.get(url);   
    
    dispacth({
        type: 'editar_subtema',
        payload: respuesta.data.subtemas
    })
}

export const eliminarSubtema = (id) => async (dispacth) => {
    
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get(url);  
    dispacth({
        type: 'eliminar_subtema',
        payload: respuesta.data.subtemas
    })
}
