import React, { Component } from 'react';
import axios from 'axios';
import CKEditor from 'ckeditor4-react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';
import Axios from 'axios';

class EditarPregunta extends Component {

    constructor(props) {
        super(props);
        this.state = {
            texto: this.props.objective,
            details: this.props.details
        };

        this.handleChange = this.handleChange.bind(this);
        this.onEditorTextoChange = this.onEditorTextoChange.bind(this);
        this.onEditorEnunciadoChange = this.onEditorEnunciadoChange.bind(this);
    }
    

    showDetails = () => {
        if(this.state.details != ''){
            return this.state.details.map((detail, index) => (
                <div key={index} className="row">
                    <div className="card-panel">
                        <div className="card-title">
                            <h6>{detail.code}</h6>
                        </div>
                        <div className="card-content">
                            <h6>{detail.content}</h6>
                        </div>
                    </div>
                </div>
            ));
        }
        
    }
    render() {

        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Oportunidad</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">                                    
                                    <div className="row">
                                        <h5>{this.state.texto}</h5>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">                            
                            <Link className="btn btn-primary btn_opts" to='/oportunidades'>Ver todas</Link>                            
                        </div>
                    </div>
                    <div className="card-panel">
                        <div className="card-title"><h5>Detalles</h5></div>
                        <div className="card-content">
                            {this.showDetails()}
                        </div>
                        <div className="card-action">                            
                            <Link className="btn btn-primary btn_opts" to='/oportunidades'>Ver todas</Link>                            
                        </div>
                    </div>
                </div>
            </div>            
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(EditarPregunta);
