import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'users';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerTodos = () => async (dispacth) => {   
    dispatch({
        type: 'cargando',
    })
    const respuesta = await instance.get();    
    
    dispacth({
        type: 'traer_usuarios',
        payload: respuesta.data.users
    })
}

export const crearUsuario = (data, usuarios) => async (dispacth) => {
    
    
    const respuesta = await instance.post(url, data);   
    usuarios.push(respuesta.data.user);
    dispacth({
        type: 'crear_usuario',
        payload: usuarios
    })
}

export const editarUsuario = (data) => async (dispacth) => {
       

    await instance.put(url, data);
    const respuesta = await instance.get(url);     

    dispacth({
        type: 'editar_usuario',
        payload: respuesta.data.users
    })
}

export const eliminarUsuario = (id) => async (dispacth) => {
    
    
    await instance.delete(url, {params: {"id": id }});
    
    const respuesta = await instance.get(url);  
    dispacth({
        type: 'eliminar_usuario',
        payload: respuesta.data.users
    })
}
