import React, { Component } from 'react';
import './IndexModulos.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as modulosActions from '../../../../actions/modulosActions';
import Spinner from '../../general/Spinner';

class IndexModulos extends Component {


    componentDidMount() {
        this.props.traerModulos(this.props.modulos);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearModulo' id="crear_modulo" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>                            
                            <th scope="col">Título del módulo</th>
                            <th scope="col">Prioridad</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }

    ponerFilas = () => this.props.modulos.map((modulo, index) => (
        <tr key={modulo.id}>

            <th scope="row">
                {index + 1}
            </th>            
            <td>
                {modulo.texto}
            </td>
            <td>
                {modulo.prioridad}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarModulo",
                    index: index,
                    id: modulo.id,
                    texto: modulo.texto,
                    prioridad: modulo.prioridad,
                    nroModulos: this.props.nroModulos
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-modulos" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de módulos</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.modulosReducers;
}
export default connect(mapToStateProps, modulosActions)(IndexModulos);
