export const traerPreguntasT = (preguntasT) => (dispacth) => {       
    
    dispacth({
        type: 'traer_preguntasT',
        payload: preguntasT
    })
}

export const crearPreguntaT = (preguntaT, preguntasT) => async (dispacth) => {
    
   
    preguntasT.push(preguntaT);
    dispacth({
        type: 'crear_preguntaT',
        payload: preguntasT
    })
}

export const editarPreguntaT = (data, preguntasT) => async (dispacth) => {
    
    preguntasT[data.index].texto = data.texto;
    preguntasT[data.index].id = data.id;
    
    dispacth({
        type: 'editar_preguntaT',
        payload: preguntasT
    })
}

export const eliminarPreguntaT = (index, preguntasT) => async (dispacth) => {
    
    preguntasT.splice(index, 1);
    dispacth({
        type: 'eliminar_preguntaT',
        payload: preguntasT
    })
}
