import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');

export const traerModulos = () => async (dispacth) => {       
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'modulos',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get();    
    
    dispacth({
        type: 'traer_modulos',
        payload: respuesta.data.modulos
    })
}

export const crearModulo = (modulo, modulos) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'modulos',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const r = await instance.post(URL_DEFAULT + 'modulos', modulo);      
    modulos.push(r.data.modulos);    
    dispacth({
        type: 'crear_modulo',
        payload: modulos
    })
}

export const editarModulo = (data, modulos) => async (dispacth) => {
    
    modulos[data.index].texto = data.texto;
    modulos[data.index].id = data.id;
    
    dispacth({
        type: 'editar_modulo',
        payload: modulos
    })
}

export const eliminarModulo = (index, modulos) => async (dispacth) => {
    
    modulos.splice(index, 1);
    dispacth({
        type: 'eliminar_modulo',
        payload: modulos
    })
}
