const INITIAL_STATE = {
    tarjetas: [
        {
            'id': 1,
            'texto': 'texto tarjeta 1'
        },
        {
            'id': 2,
            'texto': 'texto tarjeta 2'
        },
        {
            'id': 3,
            'texto': 'texto tarjeta 3'
        },
        {
            'id': 4,
            'texto': 'texto tarjeta 4'
        },
    ],
    lastId: 4
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'traer_tarjetas':
            return { ...state, tarjetas: action.payload }
        case 'crear_tarjeta':
            return {
                ...state,
                tarjetas: action.payload
            }
        case 'editar_tarjeta':
            return {
                ...state,
                tarjetas: action.payload
            }
        case 'eliminar_tarjeta':
            return {
                ...state,
                tarjetas: action.payload
            }
        default:
            return state;
    }
}
