import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');

export const traerTodos = () => async (dispacth) => {   
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'users',
        timeout: 1000,
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get(URL_DEFAULT + 'users');    
    
    dispacth({
        type: 'traer_usuarios',
        payload: respuesta.data.users
    })
}

export const crearUsuario = (data, usuarios) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'create',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });
    const respuesta = await instance.post(URL_DEFAULT + 'create', data);   
    usuarios.push(respuesta.data.user);
    dispacth({
        type: 'crear_usuario',
        payload: usuarios
    })
}

export const editarUsuario = (data) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'update',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post(URL_DEFAULT + 'update', data);
    const respuesta = await instance.get(URL_DEFAULT + 'users');     

    dispacth({
        type: 'editar_usuario',
        payload: respuesta.data.users
    })
}

export const eliminarUsuario = (id) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'delete',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post(URL_DEFAULT + 'delete', { "id": id });
    
    const respuesta = await instance.get(URL_DEFAULT + 'users');  
    dispacth({
        type: 'eliminar_usuario',
        payload: respuesta.data.users
    })
}
