const INITIAL_STATE = {
    tarjetas: [],
    cargando: false
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'traer_tarjetas':
            return { 
                ...state, 
                tarjetas: action.payload,
                cargando: false
            }
        case 'crear_tarjeta':
            return {
                ...state,
                tarjetas: action.payload
            }
        case 'editar_tarjeta':
            return {
                ...state,
                tarjetas: action.payload
            }
        case 'eliminar_tarjeta':
            return {
                ...state,
                tarjetas: action.payload
            }
        case 'cargando':
            return {
                ...state,
                cargando: true
            }
        default:
            return state;
    }
}
