import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as tarjetasActions from '../../../../actions/tarjetasActions';

class CrearTarjeta extends Component {

    constructor(props) {
        super(props);

        this.state = {
            texto: '<p>Escriba el <em>texto</em> de la <strong>tarjeta</strong></p>',
            titulo: '<p>Escriba el <em>titulo</em> de la <strong>tarjeta</strong></p>',
            imageSrc: '',
            showImage: false
        };

        this.onEditorTextoChange = this.onEditorTextoChange.bind(this);

    }


    crear = () => {

        let data = {
            "texto": this.state.texto,
            "titulo": this.state.titulo,
            "file": this.props.file,
            "type": this.props.type,
            "subtema_id": document.getElementById('subtemas').value
        }
        this.props.crearTarjeta(data, this.props.tarjetas);

    }

    onEditorTextoChange(evt) {
        this.setState({
            texto: evt.editor.getData()
        });
    }

    onEditorTituloChange(evt) {
        this.setState({
            titulo: evt.editor.getData()
        });
    }

    componentDidMount() {


    }
    handleChange = (e) => {
        var file = e.target.files[0];
        console.log(file.type);
        if (file.type == 'image/jpg' || file.type == 'image/jpeg' || file.type == 'image/png') {
            /*var output = document.getElementById('output');
            output.src = URL.createObjectURL(e.target.files[0]);*/
            this.setState({
                imageSrc: URL.createObjectURL(e.target.files[0]),
                showImage: true
            })
            window.localStorage.setItem('imgData', output.src);
            window.localStorage.setItem('size', file.size);
            this.props.setFile(file);
            this.props.setType(file.type);
        }

    }
    showImage = () => {
        if (this.state.showImage) {
            return (
                <div className="card">
                    <div className="card-image">
                        <img id="output" src={this.state.imageSrc} className="col l6" alt="imagen" />
                    </div>
                </div>
            )
        }
    }
    showSubtemas = () => this.props.subtemas.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Crear tarjeta de conocimiento</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="row">
                                        <p>Texto de la tarjeta</p>
                                        <CKEditor onBeforeLoad={(CKEDITOR) => (CKEDITOR.disableAutoInline = true)}
                                            data={this.state.texto}
                                            onChange={this.onEditorTextoChange} />
                                    </div>
                                    <div className="row">
                                        <p>Título de la tarjeta</p>
                                        <CKEditor
                                            data={this.state.titulo}
                                            onChange={this.onEditorTituloChange} />
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <p>Imagen</p>
                                            <input id="url_recurso" onChange={e => this.handleChange(e)} type="file" className="validate" />
                                        </div>
                                        {this.showImage()}
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <p>¿A qué subtema pertenece?</p>
                                            <select id="subtemas" className="browser-default">
                                                {this.showSubtemas()}
                                            </select>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/tarjetas'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/tarjetas'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.tarjetasReducers;
}
export default connect(mapToStateProps, tarjetasActions)(CrearTarjeta);
