import React, { Component } from 'react';
import Login from '../login/Login';
import Dashboard from '../dashboard/Dashboard';

class Master extends Component {
    constructor(props) {
        super(props);
        this.state = {
            login : false,
            dataUser: '',
            cargandoNetwork: false
        }
    }
    
    changeLogin = (data) => {
        console.log(data);
        this.setState({
            login: !this.state.login,
            dataUser: data,

        });
    }

    changeCargandoNet = () => {
        //console.log(data);
        this.setState({
            cargandoNetwork: !this.state.cargandoNetwork

        });
    }

    changeComponent = () => {        
        if(!this.state.login){  
            window.localStorage.clear();
            return <Login changeLogin={this.changeLogin.bind(this)}/>
        }
        return <Dashboard changeLogin={this.changeLogin.bind(this)} dataUser={this.state.dataUser} cargandoNet={this.changeCargandoNet.bind(this)} cargando={this.state.cargandoNetwork}/>
    }
    render() {
        return (
            <div>               
                {this.changeComponent()}
            </div>            
        );
    }
}
export default Master;
