export const traerPreguntas = (preguntas) => (dispacth) => {       
    
    dispacth({
        type: 'traer_preguntas',
        payload: preguntas
    })
}

export const crearPregunta = (pregunta, preguntas) => async (dispacth) => {
    
   
    preguntas.push(pregunta);
    dispacth({
        type: 'crear_pregunta',
        payload: preguntas
    })
}

export const editarPregunta = (data, preguntas) => async (dispacth) => {
    console.log(data.index);
    preguntas[data.index].texto = data.texto;
    preguntas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_pregunta',
        payload: preguntas
    })
}

export const eliminarPregunta = (id) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: 'http://localhost:8000/api/delete',
        timeout: 1000,
        headers: { 'Authorization': 'Bearer ' + token }
    });

    await instance.post('http://localhost:8000/api/delete', { "id": id });
    
    const respuesta = await instance.get('http://localhost:8000/api/users');  
    dispacth({
        type: 'eliminar_usuario',
        payload: respuesta.data.users
    })
}
