export const traerRespuestas = (respuestas) => (dispacth) => {       
    
    dispacth({
        type: 'traer_respuestas',
        payload: respuestas
    })
}

export const crearRespuesta = (respuesta, respuestas) => async (dispacth) => {
    
   
    respuestas.push(respuesta);
    dispacth({
        type: 'crear_respuesta',
        payload: respuestas
    })
}

export const editarRespuesta = (data, respuestas) => async (dispacth) => {
    
    respuestas[data.index].texto = data.texto;
    respuestas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_respuesta',
        payload: respuestas
    })
}

export const eliminarRespuesta = (index, respuestas) => async (dispacth) => {
    
    respuestas.splice(index, 1);
    dispacth({
        type: 'eliminar_respuesta',
        payload: respuestas
    })
}
