import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';

const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'tarjetas';
const urlSubtemas = URL_DEFAULT + 'subtemas';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerTarjetas = () => async (dispacth) => {       
    dispacth({
        type: 'cargando'        
    })
    const respuesta = await instance.get();    
    let nroTarjetas = respuesta.data.tarjetas.length;
    dispacth({
        type: 'traer_tarjetas',
        payload: respuesta.data.tarjetas
    })
}

export const traerSubtemas = () => async (dispacth) => {       
    const instance = axios.create({
        baseURL: urlSubtemas,
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_subtemas',
        payload: respuesta.data.subtemas
    })
}

export const crearTarjeta = (tarjeta, tarjetas) => async (dispacth) => {
    const formData = new FormData();
    formData.append('file', tarjeta.file)    
    formData.append('texto', tarjeta.texto)
    formData.append("titulo", tarjeta.titulo)
    formData.append("subtema_id", tarjeta.subtema_id)
    const r = await instance.post(url, formData);      
    tarjetas.push(r.data.tarjeta);    
    dispacth({
        type: 'crear_tarjeta',
        payload: tarjetas
    })
}

export const editarTarjeta = (data) => async (dispacth) => {
      
    await instance.put(url, data);
    const respuesta = await instance.get(url);   
    
    dispacth({
        type: 'editar_tarjeta',
        payload: respuesta.data.tarjetas
    })
}

export const eliminarTarjeta = (id) => async (dispacth) => {
    
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get(url);  
    dispacth({
        type: 'eliminar_tarjeta',
        payload: respuesta.data.tarjetas
    })
}

export const setFile = (file) => async (dispacth) => {
    console.log('setFile');
    dispacth({
        type: 'set_file',
        payload: file
    })
}

export const setType = (type) => async (dispacth) => {
    
    dispacth({
        type: 'set_type',
        payload: type
    })
}
