import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';

class EditarPregunta extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: this.props.location.texto,
            positions: [],
            positionsLastSelect: [],
            scores: [],
            scoresLastSelect: [],
            positionValue1: this.props.positionsDefaults[0],
            scoreValue1: this.props.scoresDefaults[0]           
        };

        this.handleChange = this.handleChange.bind(this);
        this.onEditorChange = this.onEditorChange.bind(this);
    }
    componentDidMount() {
        this.props.traerRespById(this.props.location.id);
        let positionsInitial = ['a', 'b', 'c'];
        let positionLastSelect = [];
        let pos = positionsInitial.indexOf(this.props.positionsDefaults[0]);
        positionsInitial.splice(pos, 1);       
        
        this.setState({
            positions: positionsInitial,
            positionsLastSelect: this.props.positionsDefaults[2]
        })
       
    }

    onEditorChange(evt) {
        this.setState({
            data: evt.editor.getData()
        });
    }

    handleChange(changeEvent) {
        this.setState({
            data: changeEvent.target.value
        });
    }

    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": this.state.data
        }

        this.props.editarPregunta(data, this.props.preguntas);
    }

    positionChange = (e, positions) => {        
        let postionsEnables = positions;
        let pos = postionsEnables.indexOf(e.target.value);
        postionsEnables.splice(pos, 1);
        if (e.target.id == 'pos_resp_1') {
            this.setState({
                positions: postionsEnables                
            });
        } else {
            this.setState({
                positionsLastSelect: postionsEnables
            });
        }
        this.props.clearPositionsDefaults();
    }

    scoreChange = (e, scores) => {
        let scoresEnables = scores;
        console.log(e.target.value);
        let pos = scoresEnables.indexOf(e.target.value);
        scoresEnables.splice(pos, 1);
        if (e.target.id == 'score_resp_1') {
            this.setState({
                scores: scoresEnables,
                scoreValue1: e.target.value
            });
        } else {
            this.setState({
                scoresLastSelect: scoresEnables
            });
        }

    }

    showOptions = (options) => (
        options.map((op, index) => (
            <option key={index} value={op}>{op}</option>
        ))
    )

    showScores = (e, item) => {

        console.log(e.target.value);
    }    


    delete = () => {
        this.props.eliminarPregunta(this.props.location.id);
    }
    render() {
        
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <label>Texto de la pregunta</label>
                                    <CKEditor
                                        data={this.state.data}
                                        onChange={this.onEditorChange} />
                                </form>
                            </div>
                        </div>
                        <div className="card-panel">
                            <div className="card-content">
                                <span className="card-title"><h4>Respuestas</h4></span>
                                <div className="row">
                                    <form className="col s12">
                                        <div className="input-field col s1">
                                            <select id="pos_resp_1" className="browser-default" value={this.props.positionsDefaults[0]} onChange={(e) => { this.positionChange(e, ['a', 'b', 'c']) }}>
                                                <option value="">Posición</option>
                                                {this.showOptions(['a', 'b', 'c'])}
                                            </select>
                                        </div>
                                        <div className="input-field col s10">
                                            <input id="texto_respuesta_1" type="text" className="validate" />
                                            <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                        </div>
                                        <div className="input-field col s1">
                                            <select id="score_resp_1" className="browser-default" value={this.props.scoresDefaults[0]} onChange={(e) => { this.scoreChange(e, ['0', '1', '2']) }}>
                                                <option value="" >Puntaje</option>
                                                {this.showOptions(['0', '1', '2'])}
                                            </select>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <div className="card-content">
                                <div className="row">
                                    <form className="col s12">
                                        <div className="input-field col s1">
                                            <select id='pos_resp_2' className="browser-default" value={this.props.positionsDefaults[1]} onChange={(e) => { this.positionChange(e, this.state.positions.slice()) }}>
                                                <option value="default">Posición</option>
                                                {this.showOptions(this.state.positions)}
                                            </select>
                                        </div>
                                        <div className="input-field col s10">
                                            <input id="texto_respuesta_2" type="text" className="validate" />
                                            <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                        </div>
                                        <div className="input-field col s1">
                                            <select id="score_resp_2" className="browser-default" value={this.props.scoresDefaults[1]} onChange={(e) => { this.scoreChange(e, this.state.scores.slice()) }}>
                                                <option value="">Puntaje</option>
                                                {this.showOptions(this.state.scores)}
                                            </select>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <div className="card-content">
                                <div className="row">
                                    <form className="col s12">
                                        <div className="input-field col s1">
                                            <select id='pos_resp_3' className="browser-default" value={this.props.positionsDefaults[2]}>
                                                <option value="">Posición</option>
                                                {this.showOptions(this.state.positionsLastSelect)}
                                            </select>
                                        </div>
                                        <div className="input-field col s10">
                                            <input id="texto_respuesta_3" type="text" className="validate" />
                                            <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                        </div>
                                        <div className="input-field col s1">
                                            <select id="score_resp_3" className="browser-default" value={this.props.scoresDefaults[2]}>
                                                <option value="default">Puntaje</option>
                                                {this.showOptions(this.state.scoresLastSelect)}
                                            </select>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <div className="card-action">
                                <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntas'>Guardar respuestas</Link>
                                <Link className="btn btn-primary btn_opts" to='/'>Cancelar</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div >
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(EditarPregunta);
