import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');

export const traerPreguntas = (preguntas) => async (dispacth) => {       
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'questions',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_preguntas',
        payload: respuesta.data.preguntas
    })
}

export const crearPregunta = (pregunta, preguntas) => async (dispacth) => {
    
   
    preguntas.push(pregunta);
    dispacth({
        type: 'crear_pregunta',
        payload: preguntas
    })
}

export const editarPregunta = (data, preguntas) => async (dispacth) => {
    console.log(data.index);
    preguntas[data.index].texto = data.texto;
    preguntas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_pregunta',
        payload: preguntas
    })
}

export const eliminarPregunta = (index, preguntas) => async (dispacth) => {
    
    preguntas.splice(index, 1);
    dispacth({
        type: 'eliminar_pregunta',
        payload: preguntas
    })
}
