import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as tarjetasActions from '../../../../actions/tarjetasActions';

class EditarTarjeta extends Component {

    constructor(props) {
        super(props);

        this.state = {
            texto: this.props.location.texto,
            titulo: this.props.location.titulo
        };

        this.onEditorTextoChange = this.onEditorTextoChange.bind(this);

    }

    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": this.props.location.texto,
            "titulo": this.props.location.titulo,
            "file": this.props.file,
            "type": this.props.type,
            "subtema_id": document.getElementById('subtemas').value,
            "prioridad": document.getElementById('show_prioridades').value
        }

        this.props.editarTarjeta(data);
    }
    delete() {

        this.props.eliminarTarjeta(this.props.location.id);
    }

    onEditorTextoChange(evt) {
        this.setState({
            texto: evt.editor.getData()
        });
    }

    onEditorTituloChange(evt) {
        this.setState({
            titulo: evt.editor.getData()
        });
    }

    showPrioridades = () => {
        let items = []
        for (let i = 0; i < this.props.location.nroTarjetas; i++) {
            items.push(<option value={i + 1} key={i}> {i + 1} </option>)
        }
        return items;
    }
    showSubtemas = () => this.props.subtemas.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))

    handleChange = (e) => {
        var file = e.target.files[0];
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(e.target.files[0]);
        window.localStorage.setItem('imgData', output.src);
        window.localStorage.setItem('size', file.size);
        this.props.setFile(file);
        this.props.setType(file.type);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Editar tarjeta de conocimiento</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <p>Prioridad</p>
                                            <select id="show_prioridades" className="browser-default" defaultValue={this.props.location.prioridad}>
                                                {this.showPrioridades()}
                                            </select>
                                        </div>
                                        <div className="row">
                                            <p>Texto de la tarjeta</p>
                                            <CKEditor
                                                data={this.state.texto}
                                                onChange={this.onEditorTextoChange} />
                                        </div>
                                        <div className="row">
                                            <p>Título de la tarjeta</p>
                                            <CKEditor
                                                data={this.state.titulo}
                                                onChange={this.onEditorTituloChange} />
                                        </div>
                                        <div className="row">
                                            <div className="input-field col s6">
                                                <p>Imagen</p>
                                                <input id="url_recurso" onChange={e => this.handleChange(e)} type="file" className="validate" />
                                            </div>
                                            <div className="card">
                                                <div className="card-image">
                                                    <img id="output" className="col l6" alt="imagen" src={this.props.location.path_imagen} />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="row">
                                            <div className="input-field col s6">
                                                <p>¿A qué subtema pertenece?</p>
                                                <select id="subtemas" className="browser-default">
                                                    {this.showSubtemas()}
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/tarjetas'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/tarjetas'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/tarjetas' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.tarjetasReducers;
}
export default connect(mapToStateProps, tarjetasActions)(EditarTarjeta);
