import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';

class EditarPregunta extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: this.props.location.texto
        };

        this.handleChange = this.handleChange.bind(this);
        this.onEditorChange = this.onEditorChange.bind(this);
    }

    onEditorChange(evt) {
        this.setState({
            data: evt.editor.getData()
        });
    }

    handleChange(changeEvent) {
        this.setState({
            data: changeEvent.target.value
        });
    }

    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": document.getElementById('text_pregunta').value
        }

        this.props.editarPregunta(data, this.props.preguntas);
    }

    delete = () => {
        this.props.eliminarPregunta(this.props.location.id);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <label>Texto de la pregunta</label>
                                    <CKEditor
                                        data={this.state.data}
                                        onChange={this.onEditorChange} />
                                </form>
                                {/*<div className="row">
                                        <div className="input-field col s6">
                                            <input id="text_pregunta" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />                                            
                                        </div>                                        
        </div>*/}
                                
                        </div>
                    </div>
                    <div className="card-action">
                        <Link className="btn btn-small btn_opts" onClick={this.editar} to='/preguntas'>Guardar</Link>
                        <Link className="btn btn-small btn_opts" to='/preguntas'>Cancelar</Link>
                        <Link className="btn btn-small btn_opts" to='/preguntas' onClick={() => { this.delete() }}>Eliminar</Link>
                    </div>
                </div>
            </div>
            </div >
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(EditarPregunta);
