export const traerSubtemas = (subtemas) => (dispacth) => {       
    
    dispacth({
        type: 'traer_subtemas',
        payload: subtemas
    })
}

export const crearSubtema = (subtema, subtemas) => async (dispacth) => {
    
   
    subtemas.push(subtema);
    dispacth({
        type: 'crear_subtema',
        payload: subtemas
    })
}

export const editarSubtema = (data, subtemas) => async (dispacth) => {
    
    subtemas[data.index].texto = data.texto;
    subtemas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_subtema',
        payload: subtemas
    })
}

export const eliminarSubtema = (index, subtemas) => async (dispacth) => {
    
    subtemas.splice(index, 1);
    dispacth({
        type: 'eliminar_subtema',
        payload: subtemas
    })
}
