import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'questionsTrivia';
const instance = axios.create({
    baseURL: url,
    headers: { 'Authorization': 'bearer ' + token }
});
export const traerPreguntasT = () => async (dispacth) => {       
    
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_preguntas',
        payload: respuesta.data.preguntas
    })
}

export const crearPregunta = (pregunta, preguntas) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'questions',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.post(URL_DEFAULT + 'questions', pregunta);   
    preguntas.push(respuesta.data.pregunta);
    dispacth({
        type: 'crear_pregunta',
        payload: preguntas
    })
}

export const editarPregunta = (data) => async (dispacth) => {
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'questions',       
        headers: { 'Authorization': 'bearer ' + token }
    });

    await instance.put(URL_DEFAULT + 'questions', data);
    const respuesta = await instance.get(URL_DEFAULT + 'questions');     
    dispacth({
        type: 'editar_pregunta',
        payload: respuesta.data.preguntas
    })
}

export const eliminarPregunta = (id) => async (dispacth) => {
   
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'questions',       
        headers: { 'Authorization': 'bearer ' + token }
    });

    await instance.delete(URL_DEFAULT + 'questions', { params: {"id": id }} );
    
    const respuesta = await instance.get(URL_DEFAULT + 'questions');  
    
    dispacth({
        type: 'eliminar_pregunta',
        payload: respuesta.data.preguntas
    })
}
