import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as recursosActions from '../../../../actions/recursosActions';

class EditarRecurso extends Component {


    editar = () => {

        let data = {
            "id": this.props.location.id,
            "name": document.getElementById('nombre_recurso').value,
            //"path": window.localStorage.getItem('imgData'),
            "size": window.localStorage.getItem('size'),
            "file": this.props.file,
            "type": this.props.type
        }

        this.props.editarRecurso(data);
    }

    delete() {

        this.props.eliminarRecurso(this.props.location.id);
    }

    handleChange = (e) => {
        var file = e.target.files[0];
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(e.target.files[0]);
        window.localStorage.setItem('imgData', output.src);
        window.localStorage.setItem('size', file.size);
        this.props.setFile(file);
        this.props.setType(file.type);
    }

    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Editar recurso</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="nombre_recurso" type="text" className="validate" placeholder={this.props.location.nombre} defaultValue={this.props.location.nombre} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="url_recurso" onChange={e => this.handleChange(e)} type="file" className="validate" />
                                        </div>
                                    </div>
                                </form>
                                <div className="card">
                                    <div className="card-image">
                                        <img id="output" src={this.props.location.url} className="col l6" alt="imagen" />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/recursos'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/recursos'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/recursos' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.recursosReducers;
}
export default connect(mapToStateProps, recursosActions)(EditarRecurso);
