import React, { Component } from 'react';
import './IndexPreguntas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';
import Spinner from '../../general/Spinner';

class IndexPreguntas extends Component {


    componentDidMount = () => {
        this.props.traerPreguntas(this.props.preguntas);
        this.props.traerModulos();
    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />;
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearPregunta' id="crear_pregunta" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped col-l12">
                    <thead>
                        <tr>
                            <th scope="col" className="col-l1">#</th>
                            <th scope="col" className="col-l4">Enunciado</th>
                            <th scope="col" className="col-l3">Texto de la pregunta</th>
                            <th scope="col" className="col-l3">Módulo</th>
                            <th scope="col" className="col-l1">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>

        )
    }

    getNombreModulo = (id) => {
        let nombre = '';
        this.props.modulos.forEach(element => {
            if (element.id == id) {
                nombre = element.texto
            }
        });
        return nombre;
    }

    ponerFilas = () => this.props.preguntas.map((pregunta, index) => (
        <tr key={pregunta.id}>

            <th scope="row">
                {index + 1}
            </th>
            <td>
                {pregunta.enunciado}
            </td>
            <td dangerouslySetInnerHTML={{ __html: pregunta.texto }}>

            </td>
            <td>
                {this.getNombreModulo(pregunta.modulo_id)}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarPregunta",
                    id: pregunta.id,
                    enunciado: pregunta.enunciado,
                    texto: pregunta.texto,
                    modulo: pregunta.modulo_id
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-preguntas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de preguntas</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(IndexPreguntas);
