import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as respuestasActions from '../../../../actions/preguntasActions';

class EditarPregunta extends Component {

    
    editar = () => {

        let data = {
            "index": this.props.location.index,
            "id": this.props.location.id,
            "texto": document.getElementById('text_respuesta').value           
        }

        this.props.editarRespuesta(data, this.props.respuestas);
    }

    componentDidMount() {
        

    }

    

    delete() {
       
            this.props.eliminarPregunta(this.props.location.index, this.props.respuestas);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="text_pregunta" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />                                            
                                        </div>                                        
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/respuestas'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/respuestas'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/respuestas' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.respuestasReducers;
}
export default connect(mapToStateProps, respuestasActions)(EditarPregunta);
