import React, { Component } from 'react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as respuestasActions from '../../../../actions/respuestasActions';

class CrearRespuesta extends Component {

    

    crear = () => {
        
        let data = {
            "id": this.props.lastId + 1,
            "texto": document.getElementById('texto_pregunta').value            
        }
        this.props.crearRespuesta(data,this.props.respuestas);
       
    }

    componentDidMount() {


    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">

                                    <div className="input-field col s6">
                                        <input id="texto_respuesta" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntas'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.respuestasReducers;
}
export default connect(mapToStateProps, respuestasActions)(CrearRespuesta);
