export const traerTarjetas = (tarjetas) => (dispacth) => {       
    
    dispacth({
        type: 'traer_tarjetas',
        payload: tarjetas
    })
}

export const crearTarjeta = (tarjeta, tarjetas) => async (dispacth) => {
    
   
    tarjetas.push(tarjeta);
    dispacth({
        type: 'crear_tarjeta',
        payload: tarjetas
    })
}

export const editarTarjeta = (data, tarjetas) => async (dispacth) => {
    
    tarjetas[data.index].texto = data.texto;
    tarjetas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_tarjeta',
        payload: tarjetas
    })
}

export const eliminarTarjeta = (index, tarjetas) => async (dispacth) => {
    
    tarjetas.splice(index, 1);
    dispacth({
        type: 'eliminar_tarjeta',
        payload: tarjetas
    })
}
